# Social Media Emotion Detector in Pyodide-compatible Python
# Rule-based approach (lightweight for browser-based execution)

# Define a simple keyword-based function
def detect_emotion(text):
    text = text.lower()
    if any(word in text for word in ["happy", "joy", "excited", "awesome", "great"]):
        return "Joy"
    elif any(word in text for word in ["sad", "depressed", "cry", "unhappy"]):
        return "Sadness"
    elif any(word in text for word in ["angry", "mad", "furious", "rage"]):
        return "Anger"
    elif any(word in text for word in ["afraid", "scared", "terrified", "anxious"]):
        return "Fear"
    elif any(word in text for word in ["surprised", "shocked", "amazed"]):
        return "Surprise"
    else:
        return "Neutral"

# Sample inputs
examples = [
    "I'm so happy and excited today!",
    "This is the worst day ever. I feel so sad.",
    "I can't believe how angry I am right now!",
    "I'm scared about the exam tomorrow.",
    "Nothing much happening. Just a regular day."
]

# Run predictions
for sentence in examples:
    emotion = detect_emotion(sentence)
    print(f"Text: {sentence}\nDetected Emotion: {emotion}\n")
